// Parse SSE response if needed
const parseSSE = (text) => {
  const lines = text.split('\n');
  for (const line of lines) {
    if (line.startsWith('data: ')) {
      const data = line.slice(6);
      if (data && data !== '[DONE]') {
        try {
          return JSON.parse(data);
        } catch (e) {
          console.error('Error parsing SSE data:', e);
        }
      }
    }
  }
  return null;
};

let contactsData = $input.first().json;
let opportunitiesData = $input.last().json;

// Handle SSE format
if (typeof contactsData === 'string') {
  contactsData = parseSSE(contactsData);
}
if (typeof opportunitiesData === 'string') {
  opportunitiesData = parseSSE(opportunitiesData);
}

// Extract nested result from tools/call response
const extractData = (response) => {
  if (response?.result?.content) {
    // tools/call format - nested JSON in text field
    const content = response.result.content;
    if (Array.isArray(content) && content[0]?.text) {
      try {
        const parsed = JSON.parse(content[0].text);
        if (parsed.data) return parsed.data;
      } catch (e) {}
    }
  }
  return response?.result || response;
};

const contactsResult = extractData(contactsData);
const opportunitiesResult = extractData(opportunitiesData);

const contacts = contactsResult?.contacts || [];
const opportunities = opportunitiesResult?.opportunities || [];

// Calculate metrics
const leads = contacts.length;
const activeOpportunities = opportunities.filter(o => 
  !['won', 'lost', 'abandoned'].includes(o.status?.toLowerCase())
);

const totalOpportunities = activeOpportunities.length;
const revenue = activeOpportunities.reduce((sum, opp) => 
  sum + (parseFloat(opp.monetaryValue) || 0), 0
);
const conversion = leads > 0 ? ((totalOpportunities / leads) * 100).toFixed(1) : 0;

// Pipeline calculations
const pipelineTotal = revenue;
const dealAverage = totalOpportunities > 0 
  ? Math.round(revenue / totalOpportunities) 
  : 0;

// At-risk deals (>30 days without activity)
const thirtyDaysAgo = new Date();
thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

const atRisk = activeOpportunities.filter(opp => {
  const lastUpdate = new Date(opp.lastStatusChangeDate || opp.updatedAt);
  return lastUpdate < thirtyDaysAgo;
}).length;

// Pipeline by stage
const stageMap = {};
activeOpportunities.forEach(opp => {
  const stage = opp.pipelineStage || opp.stageName || 'Sin etapa';
  if (!stageMap[stage]) {
    stageMap[stage] = {
      stage,
      count: 0,
      value: 0
    };
  }
  stageMap[stage].count++;
  stageMap[stage].value += parseFloat(opp.monetaryValue) || 0;
});

const pipelineByStage = Object.values(stageMap).map(s => ({
  ...s,
  percentage: totalOpportunities > 0 
    ? ((s.count / totalOpportunities) * 100).toFixed(1) 
    : 0
}));

// Generate AI insights
const insights = [];
if (atRisk > 0) {
  insights.push(`${atRisk} deals llevan más de 30 días sin actividad`);
}
if (dealAverage > 0 && dealAverage < 150000) {
  insights.push(`El ticket promedio de $${dealAverage.toLocaleString()} está por debajo del objetivo`);
}
if (conversion < 20) {
  insights.push(`Tasa de conversión del ${conversion}% puede mejorarse`);
}
if (insights.length === 0) {
  insights.push('Pipeline saludable, mantén el ritmo');
}

// Build response
return {
  json: {
    leads,
    opportunities: totalOpportunities,
    revenue: Math.round(revenue),
    conversion: parseFloat(conversion),
    pipelineTotal: Math.round(pipelineTotal),
    dealAverage,
    atRisk,
    totalDeals: totalOpportunities,
    pipelineByStage,
    insights,
    _metadata: {
      processedAt: new Date().toISOString(),
      totalContactsProcessed: leads,
      totalOpportunitiesProcessed: opportunities.length
    }
  }
};
