// Parse SSE response
const parseSSE = (text) => {
  const lines = text.split('\n');
  for (const line of lines) {
    if (line.startsWith('data: ')) {
      const data = line.slice(6);
      if (data && data !== '[DONE]') {
        try {
          return JSON.parse(data);
        } catch (e) {
          console.error('Error parsing SSE:', e);
        }
      }
    }
  }
  return null;
};

let contactsData = $input.first().json;
let opportunitiesData = $input.last().json;

if (typeof contactsData === 'string') {
  contactsData = parseSSE(contactsData);
}
if (typeof opportunitiesData === 'string') {
  opportunitiesData = parseSSE(opportunitiesData);
}

// Extract nested result from tools/call response
const extractData = (response) => {
  if (response?.result?.content) {
    const content = response.result.content;
    if (Array.isArray(content) && content[0]?.text) {
      try {
        const parsed = JSON.parse(content[0].text);
        if (parsed.data) return parsed.data;
      } catch (e) {}
    }
  }
  return response?.result || response;
};

const contactsResult = extractData(contactsData);
const opportunitiesResult = extractData(opportunitiesData);

const contacts = contactsResult?.contacts || [];
const opportunities = opportunitiesResult?.opportunities || [];

// Create opportunities map by contact
const oppsByContact = {};
opportunities.forEach(opp => {
  const contactId = opp.contactId || opp.contact_id;
  if (!oppsByContact[contactId]) {
    oppsByContact[contactId] = [];
  }
  oppsByContact[contactId].push(opp);
});

// Hot Lead Scoring Algorithm
const calculateHotScore = (contact) => {
  let score = 0;
  const reasons = [];
  const actions = [];

  // Factor 1: VIP Tags (40 points)
  const vipTags = ['VIP', 'Premium', 'Caliente', 'Hot', 'Alta prioridad'];
  const hasVIPTag = contact.tags?.some(tag => 
    vipTags.some(vip => tag.toLowerCase().includes(vip.toLowerCase()))
  );
  if (hasVIPTag) {
    score += 40;
    reasons.push('Tiene tag VIP asignado');
    actions.push('Priorizar contacto inmediato');
  }

  // Factor 2: Recent Activity (25 points)
  const lastActivity = new Date(contact.lastActivityAt || contact.dateAdded);
  const daysSinceActivity = (new Date() - lastActivity) / (1000 * 60 * 60 * 24);

  if (daysSinceActivity <= 1) {
    score += 25;
    reasons.push('Actividad en últimas 24 horas');
    actions.push('Contactar hoy mismo');
  } else if (daysSinceActivity <= 7) {
    score += 15;
    reasons.push('Actividad en última semana');
    actions.push('Programar seguimiento');
  }

  // Factor 3: Active Opportunities (20 points)
  const contactOpps = oppsByContact[contact.id] || [];
  const activeOpps = contactOpps.filter(o => 
    !['won', 'lost', 'abandoned'].includes(o.status?.toLowerCase())
  );

  if (activeOpps.length > 0) {
    const points = Math.min(activeOpps.length * 7, 20);
    score += points;
    reasons.push(`${activeOpps.length} oportunidad(es) activa(s)`);

    const highValue = activeOpps.some(o => (o.monetaryValue || 0) > 200000);
    if (highValue) {
      score += 10;
      reasons.push('Oportunidad de alto valor');
      actions.push('Enviar propuesta personalizada');
    }
  }

  // Factor 4: Contact Quality (10 points)
  if (contact.email && contact.phone) {
    score += 10;
  }

  // Factor 5: Source (5 points)
  const highValueSources = ['referido', 'referral', 'partner', 'website'];
  if (contact.source && highValueSources.some(s => 
    contact.source.toLowerCase().includes(s)
  )) {
    score += 5;
    reasons.push('Fuente de alta calidad');
  }

  if (actions.length === 0) {
    actions.push('Enviar mensaje de seguimiento');
    actions.push('Actualizar información del lead');
  }

  return {
    score: Math.min(score, 100),
    reasons,
    actions
  };
};

// Calculate scores
const scoredContacts = contacts.map(contact => {
  const scoring = calculateHotScore(contact);

  return {
    contactId: contact.id,
    name: contact.name || `${contact.firstName || ''} ${contact.lastName || ''}`.trim(),
    email: contact.email,
    phone: contact.phone,
    score: scoring.score,
    temperature: scoring.score >= 80 ? 'very-hot' : 
                 scoring.score >= 60 ? 'hot' : 'warm',
    reasons: scoring.reasons,
    suggestedActions: scoring.actions,
    opportunities: (oppsByContact[contact.id] || []).map(opp => ({
      id: opp.id,
      name: opp.name,
      value: opp.monetaryValue || 0,
      stage: opp.pipelineStage || opp.stageName || 'Sin etapa'
    })),
    tags: contact.tags || [],
    lastActivity: contact.lastActivityAt || contact.dateAdded
  };
});

const hotLeads = scoredContacts
  .filter(c => c.score >= 60)
  .sort((a, b) => b.score - a.score);

return {
  json: {
    hotLeads,
    summary: {
      total: hotLeads.length,
      veryHot: hotLeads.filter(l => l.temperature === 'very-hot').length,
      hot: hotLeads.filter(l => l.temperature === 'hot').length,
      totalPotentialValue: hotLeads.reduce((sum, lead) => {
        const oppValue = lead.opportunities.reduce((s, o) => s + o.value, 0);
        return sum + oppValue;
      }, 0)
    },
    _metadata: {
      processedAt: new Date().toISOString(),
      totalContactsAnalyzed: contacts.length,
      scoringAlgorithm: 'v1-5factors'
    }
  }
};
