// Parse SSE response if needed
const parseSSE = (text) => {
  const lines = text.split('\n');
  for (const line of lines) {
    if (line.startsWith('data: ')) {
      const data = line.slice(6);
      if (data && data !== '[DONE]') {
        try {
          return JSON.parse(data);
        } catch (e) {
          console.error('Error parsing SSE:', e);
        }
      }
    }
  }
  return null;
};

let opportunitiesData = $input.first().json;
let pipelinesData = $input.last().json;

// Handle SSE format
if (typeof opportunitiesData === 'string') {
  opportunitiesData = parseSSE(opportunitiesData);
}
if (typeof pipelinesData === 'string') {
  pipelinesData = parseSSE(pipelinesData);
}

// Extract results
const opportunities = opportunitiesData?.result?.opportunities || [];
const pipelines = pipelinesData?.result?.pipelines || [];

// Filter only active opportunities
const activeOpportunities = opportunities.filter(opp => 
  !['won', 'lost', 'abandoned'].includes(opp.status?.toLowerCase())
);

// Get pipeline stages configuration
const pipelineStages = [];
if (pipelines.length > 0) {
  const mainPipeline = pipelines[0];
  if (mainPipeline.stages) {
    mainPipeline.stages.forEach(stage => {
      pipelineStages.push({
        id: stage.id,
        name: stage.name,
        order: stage.order || 0
      });
    });
  }
}

// If no stages from API, use defaults
if (pipelineStages.length === 0) {
  pipelineStages.push(
    { id: 'nuevo', name: 'Nuevo', order: 0 },
    { id: 'contactado', name: 'Contactado', order: 1 },
    { id: 'calificado', name: 'Calificado', order: 2 },
    { id: 'propuesta', name: 'Propuesta', order: 3 },
    { id: 'negociacion', name: 'NegociaciÃ³n', order: 4 },
    { id: 'cierre', name: 'Cierre', order: 5 }
  );
}

// Group opportunities by stage
const stageMap = {};
pipelineStages.forEach(stage => {
  stageMap[stage.id] = {
    id: stage.id,
    name: stage.name,
    order: stage.order,
    deals: []
  };
});

// Add catch-all for undefined stages
stageMap['undefined'] = {
  id: 'undefined',
  name: 'Sin Etapa',
  order: 999,
  deals: []
};

// Calculate stale threshold (30 days)
const thirtyDaysAgo = new Date();
thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

// Organize deals by stage
activeOpportunities.forEach(opp => {
  const stageId = opp.pipelineStageId || opp.stageId || 'undefined';
  const lastUpdate = new Date(opp.lastStatusChangeDate || opp.updatedAt || opp.createdAt);
  const daysInStage = Math.floor((new Date() - lastUpdate) / (1000 * 60 * 60 * 24));
  const isStale = lastUpdate < thirtyDaysAgo;

  const deal = {
    id: opp.id,
    contactName: opp.contactName || opp.contact?.name || 'Sin nombre',
    contactId: opp.contactId || opp.contact?.id,
    value: parseFloat(opp.monetaryValue) || 0,
    createdAt: opp.createdAt,
    lastActivity: opp.lastStatusChangeDate || opp.updatedAt,
    daysInStage,
    isStale,
    probability: opp.pipelineStageProbability || 0,
    status: opp.status,
    source: opp.source,
    assignedTo: opp.assignedTo
  };

  if (stageMap[stageId]) {
    stageMap[stageId].deals.push(deal);
  } else {
    stageMap['undefined'].deals.push(deal);
  }
});

// Convert to array and sort by order
const stages = Object.values(stageMap)
  .filter(stage => stage.deals.length > 0 || stage.id !== 'undefined')
  .sort((a, b) => a.order - b.order);

// Calculate summary
const totalValue = activeOpportunities.reduce((sum, opp) => 
  sum + (parseFloat(opp.monetaryValue) || 0), 0
);

const totalDeals = activeOpportunities.length;
const avgDealValue = totalDeals > 0 ? Math.round(totalValue / totalDeals) : 0;
const staleDeals = activeOpportunities.filter(opp => {
  const lastUpdate = new Date(opp.lastStatusChangeDate || opp.updatedAt);
  return lastUpdate < thirtyDaysAgo;
}).length;

return {
  json: {
    stages,
    summary: {
      totalValue: Math.round(totalValue),
      totalDeals,
      avgDealValue,
      staleDeals,
      stalePercentage: totalDeals > 0 ? ((staleDeals / totalDeals) * 100).toFixed(1) : 0
    },
    _metadata: {
      processedAt: new Date().toISOString(),
      totalOpportunitiesProcessed: opportunities.length,
      activeOpportunities: totalDeals,
      stagesCount: stages.length
    }
  }
};
