// Parse SSE response
const parseSSE = (text) => {
  const lines = text.split('\n');
  for (const line of lines) {
    if (line.startsWith('data: ')) {
      const data = line.slice(6);
      if (data && data !== '[DONE]') {
        try {
          return JSON.parse(data);
        } catch (e) {
          console.error('Error parsing SSE:', e);
        }
      }
    }
  }
  return null;
};

let contactsData = $input.first().json;
let opportunitiesData = $input.last().json;

if (typeof contactsData === 'string') {
  contactsData = parseSSE(contactsData);
}
if (typeof opportunitiesData === 'string') {
  opportunitiesData = parseSSE(opportunitiesData);
}

const contacts = contactsData?.result?.contacts || [];
const opportunities = opportunitiesData?.result?.opportunities || [];

// Create opportunities map by contact
const oppsByContact = {};
opportunities.forEach(opp => {
  const contactId = opp.contactId || opp.contact_id;
  if (!oppsByContact[contactId]) {
    oppsByContact[contactId] = {
      count: 0,
      totalValue: 0,
      opportunities: []
    };
  }
  oppsByContact[contactId].count++;
  oppsByContact[contactId].totalValue += parseFloat(opp.monetaryValue) || 0;
  oppsByContact[contactId].opportunities.push(opp);
});

// Process contacts
const processedContacts = contacts.map(contact => {
  const contactOpps = oppsByContact[contact.id] || { count: 0, totalValue: 0 };

  return {
    id: contact.id,
    name: contact.name || `${contact.firstName || ''} ${contact.lastName || ''}`.trim() || 'Sin nombre',
    firstName: contact.firstName,
    lastName: contact.lastName,
    email: contact.email,
    phone: contact.phone,
    tags: contact.tags || [],
    dateAdded: contact.dateAdded || contact.createdAt,
    lastActivity: contact.lastActivityAt || contact.dateAdded,
    opportunitiesCount: contactOpps.count,
    totalValue: Math.round(contactOpps.totalValue),
    source: contact.source,
    assignedTo: contact.assignedTo,
    country: contact.country,
    city: contact.city,
    state: contact.state,
    customFields: contact.customFields || {}
  };
});

// Sort by last activity (most recent first)
processedContacts.sort((a, b) => {
  const dateA = new Date(a.lastActivity || 0);
  const dateB = new Date(b.lastActivity || 0);
  return dateB - dateA;
});

// Apply search filter if provided
let filteredContacts = processedContacts;
const searchQuery = $json.query?.search?.toLowerCase();

if (searchQuery) {
  filteredContacts = processedContacts.filter(contact => 
    contact.name?.toLowerCase().includes(searchQuery) ||
    contact.email?.toLowerCase().includes(searchQuery) ||
    contact.phone?.includes(searchQuery)
  );
}

return {
  json: {
    contacts: filteredContacts,
    total: filteredContacts.length,
    summary: {
      totalContacts: filteredContacts.length,
      withEmail: filteredContacts.filter(c => c.email).length,
      withPhone: filteredContacts.filter(c => c.phone).length,
      withOpportunities: filteredContacts.filter(c => c.opportunitiesCount > 0).length,
      totalValue: filteredContacts.reduce((sum, c) => sum + c.totalValue, 0)
    },
    _metadata: {
      processedAt: new Date().toISOString(),
      totalContactsProcessed: contacts.length,
      searchApplied: !!searchQuery,
      searchQuery: searchQuery || null
    }
  }
};
